mapper.insert:
insert into alarm (device_id, mp_id,description ,status, alarm_time, create_time, update_time)
values
    (#{deviceId},#{mpId},#{description},#{status},#{alarmTime},#{createTime},#{updateTime});

mapper.update:
update alarm
device_id = #{deviceId}, mp_id=#{mpId}, description=#{description}, status=#{status}, alarm_time=#{alarmTime}, create_time=#{createTime}, update_time=#{updateTime},
where alarm_id = #{alarmId}

mapper.select:
select a.*,d.name as deviceName,a.status as status
from `alarm` a
left outer join `device` d on a.device_id=d.device_id

    and d.device_type=#{deviceType}
    and a.status=#{status}
    and alarm_time >= #{startTime} 
    and alarm_time <= #{endTime}

order by alarm_time desc

select count(*)
from `alarm`
where alarm_time >= #{startTime} and alarm_time <= #{endTime}

select count(*)
from `alarm`
where processed_time >= #{startTime} and processed_time <= #{endTime}
