mapper.insert:
insert into alarm (device_id, mp_id, status, alarm_time, create_time, update_time)
values
    (#{deviceId},#{mpId},#{status},#{alarmTime},#{createTime},#{updateTime});

mapper.update:
update alarm
device_id = #{deviceId}, mp_id=#{mpId}, status=#{status}, alarm_time=#{alarmTime}, create_time=#{createTime}, update_time=#{updateTime},
where alarm_id = #{alarmId}
